package ga.scrumplex.ml.sprum.sprummlbot;

import java.io.IOException;
import java.io.OutputStream;
import java.io.UnsupportedEncodingException;
import java.net.URLDecoder;
import java.util.HashMap;

import com.sun.net.httpserver.HttpExchange;
import com.sun.net.httpserver.HttpHandler;

import ga.scrumplex.ml.sprum.sprummlbot.web.func.action;


class WebGUIHandler implements HttpHandler
{	@Override
	public void handle(HttpExchange gui) throws IOException
	{
		String response = "404";
		gui.getResponseHeaders().add("Content-type", "text/html");
		
		String requestURI = gui.getRequestURI().toString();
		HashMap<String, String> args = new HashMap<>();
		String url = requestURI;
		
		if(requestURI.contains("!")) {
			String argsraw = requestURI.split("!")[1];
			String[] notargs = argsraw.split(",");
			
			
			for(String argsy : notargs) {
				String[] arg = argsy.split("=");
				args.put(arg[0], arg[1]);
			}
			
			url = requestURI.split("!")[0];
		}
		
		if(!url.endsWith("/")) {
			url = url + "/";
		}
		
		switch(url) {
		case "/":
			response = new ga.scrumplex.ml.sprum.sprummlbot.web.index().content;
			break;
			
		case "/manage/":
			response = new ga.scrumplex.ml.sprum.sprummlbot.web.manage.index(gui).content;
			break;
			
		case "/manage/shutdown/":
			response = action.shutdown();
			break;
			
		case "/manage/action/":
			response = new ga.scrumplex.ml.sprum.sprummlbot.web.index().content;
			break;
			
		case "/manage/action/ban/":
		    response = action.ban(Integer.parseInt(args.get("userid")), convertToNormalText(args.get("msg")), Integer.parseInt(args.get("time")));
			break;
			
		case "/manage/listclients/":
		    response = action.listclients();
			break;
			
		case "/manage/log/":
		    response = new ga.scrumplex.ml.sprum.sprummlbot.web.manage.log().content;
			break;
			
		case "/manage/action/sendmessage/":
			response = action.sendServerMSG(convertToNormalText(args.get("msg")));
			break;
	    
		case "/manage/action/sendprivmessage/":
			response = action.sendPrivateMSG(convertToNormalText(args.get("msg")), Integer.parseInt(args.get("userid")));
			break;
			
		case "/manage/action/kick/":
		    response = action.kick(Integer.parseInt(args.get("userid")), convertToNormalText(args.get("msg")));
		    break;
	    
		case "/manage/action/clearaccounts/":
			response = action.clearaccounts();
			break;
		    
		case "/catchmeifyoucan/":
			response = "<img src=\"data:image/jpeg;base64,\" alt=\"hi m8 xdddd\">";
			break;
		    
		case "/logout/":
			response = new ga.scrumplex.ml.sprum.sprummlbot.web.logout(gui.getPrincipal().getUsername()).content;
			break;
		    
		}
	
		if (response == "404") {
			response = "<h2 style=\"text-align:center\">404 - Resource not found</h2>";
			gui.sendResponseHeaders(404, response.length());
		} else {
			gui.sendResponseHeaders(200, response.length());
		}

		OutputStream os = gui.getResponseBody();
		os.write(response.getBytes());
    	os.close();
	}
	
	public static String convertToNormalText(String s) throws UnsupportedEncodingException {
	    String str = s.replaceAll("%20", " ");
	    /*
	    str = str.replaceAll("%C3%BC", "ü");
	    str = str.replaceAll("%C3%A4", "ä");
	    str = str.replaceAll("%C3%B6", "ö");
	    str = str.replaceAll("%C3%9C", "Ü");
	    str = str.replaceAll("%C3%84", "Ä");
	    str = str.replaceAll("%C3%96", "Ö");
	    str = str.replaceAll("%C3%9F", "ß");
	    */
	    str = URLDecoder.decode(str, "UTF-8");
	    return str;
	}
	
}